mapper.update:
UPDATE TB_USER_COPY SET
PRO_STATE = #{proState},
PRO_JIPBANK = #{proJipbank}, PRO_JEINET = #{proJeinet}, PRO_JNTIS_RND = #{proJntisRnd}, PRO_JNTIS_COMPANY = #{proJntisCompany}, PRO_JNTIS_EDUCATION = #{proJntisEducation}, PRO_JNTIS_CONSULTING = #{proJntisConsulting}, PRO_JNTIS_MARKETING = #{proJntisMarketing}, PRO_JNTIS_PLANNING = #{proJntisPlanning}, PRO_JNTIS_ETC = #{proJntisEtc}, PRO_USER_INTEREST_WORLD = #{proUserInterestWorld}, PRO_SMB_CODE1 = #{code_b}, PRO_SMB_CODE2 = #{code_m}, PRO_SMB_CODE3 = #{code_s}, PRO_SMB_TEAM1 = #{team_1}, PRO_SMB_TEAM2 = #{team_2}, PRO_SMB_TEAM3 = #{team_3}, PRO_SMB_TEAM4 = #{team_4}, PRO_SMB_TEAM5 = #{team_5}, PRO_SMB_TEAM6 = #{team_6}, PRO_SMB_TEAM7 = #{team_7}, PRO_SMB_TEAM8 = #{team_8}
WHERE USER_IDX = #{userIdx}


UPDATE TB_USER_COPY SET
			PRO_STATE = #{mode}
WHERE USER_IDX = #{userIdx}

mapper.select:
SELECT U.USER_IDX, USER_ID, U.PSN_NM, U.PRO_STATE, B.ORG_GRP_NM FROM TB_USER_COPY AS U LEFT JOIN TB_ORG_GRP B ON U.ORG_GRP_CD = B.ORG_GRP_CD WHERE U.DELETE_YN != 'Y'
AND (U.PRO_STATE LIKE 'N' OR U.PRO_STATE IS NULL OR U.PRO_STATE LIKE '')
AND U.ORG_GRP_CD = #{listOrgGrpCd}
AND U.PSN_NM LIKE '%${searchWord}%'
AND U.USER_ID LIKE '%${searchWord}%'
ORDER BY U.PSN_NM DESC LIMIT ${firstRecord} , ${pageSize}
