SELECT A.USE_STATUS_IDX, A.JOINT_YEAR, A.JEINET_EQU_NO, A.NTIS_EQU_NO, A.JOINT_USE_SECTION, A.JOINT_USE_INSTITUTION, A.DEPARTMENT, CONVERT(A.JOINT_USE_ST_DT,CHAR) AS JOINT_USE_ST_DT, CONVERT(A.JOINT_USE_ED_DT,CHAR) AS JOINT_USE_ED_DT, A.USER_NAME, A.USER_EMAIL, A.USER_PHONE, A.USE_TIME, A.EARNINGS, A.SAMPLE_COUNT, A.STATE, A.REJECT, B.KOR_NM, B.ENG_NM, C.RND_EQU_IDX, C.OWN_ORG FROM TB_USE_STATUS A LEFT JOIN TB_EQU_INFO B ON JEINET_EQU_NO = B.RND_EQU_IDX LEFT JOIN TB_EQU_GOODS C ON B.RND_EQU_IDX = C.RND_EQU_IDX WHERE 1 = 1
AND A.JEINET_EQU_NO = #{searchWord}
AND A.JOINT_USE_INSTITUTION LIKE CONCAT('%',#{searchWord},'%')
AND B.KOR_NM LIKE CONCAT('%',#{searchWord},'%')
AND A.JOINT_USE_INSTITUTION LIKE CONCAT('%',#{orgGrpNm},'%')
AND A.STATE = #{stateType}
AND A.JOINT_YEAR = #{useYear}
ORDER BY USE_STATUS_IDX DESC LIMIT ${firstRecord} , ${pageSize}

SELECT CAST(IFNULL(SUM(EQ_INFO_CNT), 0) as unsigned) as cnt1 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '1' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt2 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '2' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt3 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '3' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt4 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '4' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt5 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '5' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt6 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '6' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt7 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '7' THEN EQ_INFO_CNT ELSE null END), 0) as unsigned) as cnt8 , CAST(IFNULL(SUM(EQ_INFO_PRICE), 0) as unsigned) as price1 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '1' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price2 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '2' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price3 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '3' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price4 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '4' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price5 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '5' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price6 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '6' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price7 , CAST(IFNULL(SUM(CASE WHEN EQ_DIV_6T = '7' THEN EQ_INFO_PRICE ELSE null END), 0) as unsigned) as price8 FROM TB_USE_STATUS_TOTAL WHERE 1=1
AND EQ_REG_JEINET_NUM is not NULL AND EQ_REG_JEINET_NUM != ''
AND EQ_REG_ZEUS_NUM is not NULL AND EQ_REG_ZEUS_NUM != ''
AND EQ_REG_ETUBE_NUM is not NULL AND EQ_REG_ETUBE_NUM != ''
AND ORG_GRP_CD = #{orgGrpCd}
AND JOINT_YEAR = #{jointYear}

SELECT CAST(T.eqYcnt as unsigned) as cnt1 , CAST(T.eqNcnt as unsigned) as cnt2 , CAST((T.eqYcnt + T.eqNcnt) as unsigned) as cnt3 , CAST(ROUND(IFNULL(T.eqYcnt/T.eqNcnt*100, 0), 2) as unsigned) as cnt4 , CAST(T.pubYcnt as unsigned) as cnt5 , CAST(T.pubNcnt as unsigned) as cnt6 , CAST((T.pubYcnt + T.pubNcnt) as unsigned) as cnt7 , CAST(ROUND(IFNULL(T.pubYcnt/T.pubNcnt*100, 0), 2) as unsigned) as cnt8 , CAST(T.eqYprice as unsigned) as price1 , CAST(T.eqNprice as unsigned) as price2 , CAST((T.eqYprice + T.eqNprice) as unsigned) as price3 , CAST(ROUND(IFNULL(T.eqYprice/T.eqNprice*100, 0), 2) as unsigned) as price4 , CAST(T.pubYprice as unsigned) as price5 , CAST(T.pubNprice as unsigned) as price6 , CAST((T.pubYprice + T.pubNprice) as unsigned) as price7 , CAST(ROUND(IFNULL(T.pubYprice/T.pubNprice*100,0), 2) as unsigned) as price8 FROM ( SELECT IFNULL(SUM(CASE WHEN YEAR_EQ_USE='Y' THEN EQ_INFO_CNT ELSE null END), 0) as eqYcnt , IFNULL(SUM(CASE WHEN YEAR_EQ_USE='N' THEN EQ_INFO_CNT ELSE null END), 0) as eqNcnt , IFNULL(SUM(CASE WHEN YEAR_PUBLIC_USE='Y' THEN EQ_INFO_CNT ELSE null END), 0) as pubYcnt , IFNULL(SUM(CASE WHEN YEAR_PUBLIC_USE='N' THEN EQ_INFO_CNT ELSE null END), 0) as pubNcnt , IFNULL(SUM(CASE WHEN YEAR_EQ_USE='Y' THEN EQ_INFO_PRICE ELSE null END), 0) as eqYprice , IFNULL(SUM(CASE WHEN YEAR_EQ_USE='N' THEN EQ_INFO_PRICE ELSE null END), 0) as eqNprice , IFNULL(SUM(CASE WHEN YEAR_PUBLIC_USE='Y' THEN EQ_INFO_PRICE ELSE null END), 0) as pubYprice , IFNULL(SUM(CASE WHEN YEAR_PUBLIC_USE='N' THEN EQ_INFO_PRICE ELSE null END), 0) as pubNprice FROM TB_USE_STATUS_TOTAL WHERE 1=1
AND ORG_GRP_CD = #{orgGrpCd}
AND JOINT_YEAR = #{jointYear}
AND EQ_REG_JEINET_NUM is not NULL AND EQ_REG_JEINET_NUM != ''
AND EQ_REG_ZEUS_NUM is not NULL AND EQ_REG_ZEUS_NUM != ''
AND EQ_REG_ETUBE_NUM is not NULL AND EQ_REG_ETUBE_NUM != ''
) T

SELECT 'type0' as eqInfoPriceType , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM in ('1', '2', '3','4'), T.EQ_INFO_CNT, null)),0) as unsigned) as cnt0 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='1', T.EQ_INFO_CNT, null)),0) as unsigned) as cnt1 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='2', T.EQ_INFO_CNT, null)),0) as unsigned) as cnt2 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='3', T.EQ_INFO_CNT, null)),0) as unsigned) as cnt3 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='4', T.EQ_INFO_CNT, null)),0) as unsigned) as cnt4 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM in ('1', '2', '3','4'), EQ_INFO_PRICE, null)),0) as unsigned) as sum0 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='1', T.EQ_INFO_PRICE, null)),0) as unsigned) as sum1 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='2', T.EQ_INFO_PRICE, null)),0) as unsigned) as sum2 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='3', T.EQ_INFO_PRICE, null)),0) as unsigned) as sum3 , CAST(IFNULL(SUM(IF(T.EQ_DIV_USE_NM='4', T.EQ_INFO_PRICE, null)),0) as unsigned) as sum4 FROM TB_USE_STATUS_TOTAL T WHERE 1=1
AND ORG_GRP_CD = #{orgGrpCd}
AND JOINT_YEAR = #{jointYear}
AND EQ_REG_JEINET_NUM is not NULL AND EQ_REG_JEINET_NUM != ''
AND EQ_REG_ZEUS_NUM is not NULL AND EQ_REG_ZEUS_NUM != ''
AND EQ_REG_ETUBE_NUM is not NULL AND EQ_REG_ETUBE_NUM != ''
UNION ALL SELECT T1.eqInfoPriceType , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM in ('1', '2', '3','4'), EQ_INFO_CNT, null)),0) as unsigned) as cnt0 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='1', EQ_INFO_CNT, null)),0) as unsigned) as cnt1 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='2', EQ_INFO_CNT, null)),0) as unsigned) as cnt2 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='3', EQ_INFO_CNT, null)),0) as unsigned) as cnt3 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='4', EQ_INFO_CNT, null)),0) as unsigned) as cnt4 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM in ('1', '2', '3','4'), EQ_INFO_PRICE, null)),0) as unsigned) as sum0 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='1', EQ_INFO_PRICE, null)),0) as unsigned) as sum1 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='2', EQ_INFO_PRICE, null)),0) as unsigned) as sum2 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='3', EQ_INFO_PRICE, null)),0) as unsigned) as sum3 , CAST(IFNULL(SUM(IF(EQ_DIV_USE_NM='4', EQ_INFO_PRICE, null)),0) as unsigned) as sum4 FROM ( SELECT 'type1' as eqInfoPriceType FROM dual UNION ALL SELECT 'type2' as eqInfoPriceType FROM dual UNION ALL SELECT 'type3' as eqInfoPriceType FROM dual UNION ALL SELECT 'type4' as eqInfoPriceType FROM dual ) T1 LEFT JOIN ( SELECT CASE WHEN EQ_INFO_PRICE >= 10000000 AND EQ_INFO_PRICE < 30000000 THEN 'type1' WHEN EQ_INFO_PRICE >= 30000000 AND EQ_INFO_PRICE < 100000000 THEN 'type2' WHEN EQ_INFO_PRICE >= 100000000 AND EQ_INFO_PRICE < 500000000 THEN 'type3' WHEN EQ_INFO_PRICE >= 500000000 THEN 'type4' END as eqInfoPriceType , EQ_DIV_USE_NM , EQ_INFO_PRICE , EQ_INFO_CNT FROM TB_USE_STATUS_TOTAL WHERE 1=1
AND ORG_GRP_CD = #{orgGrpCd}
AND JOINT_YEAR = #{jointYear}
AND EQ_REG_JEINET_NUM is not NULL AND EQ_REG_JEINET_NUM != ''
AND EQ_REG_ZEUS_NUM is not NULL AND EQ_REG_ZEUS_NUM != ''
AND EQ_REG_ETUBE_NUM is not NULL AND EQ_REG_ETUBE_NUM != ''
) T2 ON T1.eqInfoPriceType = T2.eqInfoPriceType GROUP BY eqInfoPriceType

