mapper.select:
SELECT A.ARTICLE_ID, A.SITE_ID, A.BOARD_ID, A.ARTICLE_GROUP_ID, A.ARTICLE_NEXT_ID, A.ARTICLE_PARNTS_ID, A.ARTICLE_SEQ, A.DEPTH, A.CATEGORY_NM, A.SUBJECT, A.WRITER_ID, A.WRITER_NM, A.PASSWORD, A.CONTENT, A.ATCH_FILE_ID, A.THUMB_ATCH_FILE_ID, A.NOTICE_AT, A.SECRET_AT, A.DELETED_AT, A.HITS, A.DATA01, A.DATA02, A.DATA03, A.DATA04, A.DATA05, A.DATA06, A.DATA07, A.DATA08, A.DATA09, A.DATA10, A.DATA11, A.DATA12, A.DATA13, A.DATA14, A.DATA15, A.DATA16, A.DATA17, A.DATA18, A.DATA19, A.DATA20, A.NUM1, A.NUM2, A.NUM3, A.NUM4, A.NUM5, A.TEXT1, A.TEXT2, A.TEXT3, A.TEXT4, A.TEXT5, A.SHARE_BOARD_IDS, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d') AS FRST_REGIST_PNTTM, A.FRST_REGUSR_IP, A.LAST_UPDUSR_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_IP, B.FILE_ID, B.DATA_GRP, B.DATA_IDX, B.FILE_SEQ, B.FILE_CAT, B.TITLE, B.REGIST_DT, B.DOWN_CNT, B.DELETE_YN, B.PHOTO_CAT, B.FILE_PATH, B.FILE_SIZE, B.TITLE_ORG FROM JMY_BOARD_ARTICLE A LEFT JOIN TB_JNSP_FILE B ON B.DATA_IDX = A.ARTICLE_ID AND B.FILE_CAT != 'pdf' WHERE A.BOARD_ID = #{boardComIdx}
AND A.DATA06 = #{searchState}
AND DATA03 IN ('POLICY','LAWORD','ETC','STATS','TECHNOLOGY','MENUAL','BROCHURE')
AND A.DATA03 in ('POLICY', 'LAWORD', 'ETC', 'STATS')
AND A.DATA03 = #{sclas}
AND A.SUBJECT LIKE '%${searchWord}%'
AND A.CONTENT LIKE '%${searchWord}%'
AND A.DATA04 LIKE '%${searchWord}%'
AND B.DATA_IDX = #{boardIdx}
AND B.DATA_GRP = #{dataGrp}
ORDER BY A.FRST_REGIST_PNTTM DESC
ORDER BY A.HITS DESC
ORDER BY B.DOWN_CNT DESC
LIMIT ${firstRecord} , ${pageSize}

SELECT COUNT(DISTINCT ARTICLE_ID) FROM JMY_BOARD_ARTICLE A LEFT JOIN TB_JNSP_FILE B ON B.DATA_IDX = A.ARTICLE_ID WHERE A.BOARD_ID = #{boardComIdx}
AND A.DATA06 = #{searchState}
AND DATA03 IN ('POLICY','LAWORD','ETC','STATS','TECHNOLOGY','MENUAL','BROCHURE')
AND A.DATA03 in ('POLICY', 'LAWORD', 'ETC', 'STATS')
AND A.DATA03 = #{sclas}
AND A.SUBJECT LIKE '%${searchWord}%'
AND A.CONTENT LIKE '%${searchWord}%'
AND A.DATA04 LIKE '%${searchWord}%'
AND B.DATA_IDX = #{boardIdx}
AND B.DATA_GRP = #{dataGrp}
ORDER BY A.FRST_REGIST_PNTTM DESC
ORDER BY A.HITS DESC
ORDER BY B.DOWN_CNT DESC

SELECT A.ARTICLE_ID, A.SITE_ID, A.BOARD_ID, A.ARTICLE_GROUP_ID, A.ARTICLE_NEXT_ID, A.ARTICLE_PARNTS_ID, A.ARTICLE_SEQ, A.DEPTH, A.CATEGORY_NM, A.SUBJECT, A.WRITER_ID, A.WRITER_NM, A.PASSWORD, A.CONTENT, A.ATCH_FILE_ID, A.THUMB_ATCH_FILE_ID, A.NOTICE_AT, A.SECRET_AT, A.DELETED_AT, A.HITS, A.DATA01, A.DATA02, A.DATA03, A.DATA04, A.DATA05, A.DATA06, A.DATA07, A.DATA08, A.DATA09, A.DATA10, A.DATA11, A.DATA12, A.DATA13, A.DATA14, A.DATA15, A.DATA16, A.DATA17, A.DATA18, A.DATA19, A.DATA20, A.NUM1, A.NUM2, A.NUM3, A.NUM4, A.NUM5, A.TEXT1, A.TEXT2, A.TEXT3, A.TEXT4, A.TEXT5, A.SHARE_BOARD_IDS, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d') AS FRST_REGIST_PNTTM, A.FRST_REGUSR_IP, A.LAST_UPDUSR_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_IP, B.FILE_ID, B.DATA_GRP, B.DATA_IDX, B.FILE_SEQ, B.FILE_CAT, B.TITLE, B.REGIST_DT, B.DOWN_CNT, B.DELETE_YN, B.PHOTO_CAT, B.FILE_PATH, B.FILE_SIZE, B.TITLE_ORG FROM JMY_BOARD_ARTICLE A LEFT JOIN TB_JNSP_FILE B ON B.DATA_IDX = A.ARTICLE_ID AND B.FILE_CAT != 'pdf' WHERE A.BOARD_ID = #{boardComIdx}
AND A.DATA06 = #{searchState}
AND DATA03 IN ('POLICY','LAWORD','ETC','STATS','TECHNOLOGY','MENUAL','BROCHURE')
AND A.DATA03 in ('POLICY', 'LAWORD', 'ETC', 'STATS')
AND A.DATA03 = #{sclas}
AND A.SUBJECT LIKE '%${searchWord}%'
AND A.CONTENT LIKE '%${searchWord}%'
AND A.DATA04 LIKE '%${searchWord}%'
AND B.DATA_IDX = #{boardIdx}
AND B.DATA_GRP = #{dataGrp}
ORDER BY A.FRST_REGIST_PNTTM DESC
ORDER BY A.HITS DESC
ORDER BY B.DOWN_CNT DESC
LIMIT ${firstRecord} , ${pageSize}

mapper.update:

UPDATE JMY_BOARD_ARTICLE SET
	HITS = HITS+1
WHERE
		BOARD_ID = #{boardComIdx}
AND
		ARTICLE_ID = #{boardIdx}
